import { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    BarElement,
    CategoryScale,
    LinearScale,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const DashboardPage = () => {
    const [chartData, setChartData] = useState(null);
    const [reports, setReports] = useState([]);
    const [error, setError] = useState('');
    const token = localStorage.getItem('token');

    useEffect(() => {
        if (!token) return;

        const fetchReports = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/reports', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                // For chart
                const counts = {};
                res.data.forEach((report) => {
                    const categoryName = report.category.name;
                    counts[categoryName] = (counts[categoryName] || 0) + 1;
                });

                const labels = Object.keys(counts).map(label =>
                    label.charAt(0).toUpperCase() + label.slice(1).toLowerCase()
                );
                const values = Object.values(counts);

                setChartData({
                    labels: labels,
                    datasets: [
                        {
                            label: 'Report Count by Category',
                            data: values,
                            backgroundColor: '#82ca9d',
                            borderColor: '#5aa17f',
                            borderWidth: 1,
                        },
                    ],
                });

                // Store full reports
                setReports(res.data);
            } catch (err) {
                setError('Failed to load reports');
                console.error(err);
            }
        };

        fetchReports();
    }, [token]);

    // Table styles
    const thStyle = {
        borderBottom: '1px solid #ccc',
        textAlign: 'left',
        padding: '8px',
        backgroundColor: '#f4f4f4',
    };

    const tdStyle = {
        borderBottom: '1px solid #eee',
        padding: '8px',
    };

    return (
        <>
            {/* ✅ NAVBAR */}
            <nav style={{
                backgroundColor: '#2c3e50',
                color: 'white',
                padding: '15px 30px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
            }}>
                <h1 style={{ margin: 0 }}>SafeCity</h1>
                <div>
                    {token ? (
                        <button
                            style={{
                                backgroundColor: '#e74c3c',
                                color: 'white',
                                border: 'none',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                            onClick={() => {
                                localStorage.removeItem('token');
                                window.location.reload();
                            }}
                        >
                            Logout
                        </button>
                    ) : (
                        <button
                            style={{
                                backgroundColor: '#3498db',
                                color: 'white',
                                border: 'none',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                            onClick={() => window.location.href = '/login'}
                        >
                            Login
                        </button>
                    )}
                </div>
            </nav>

            {/* ✅ CONTENT */}
            <div style={{ width: '50%', marginLeft: '0', padding: '20px' }}>
                <h2>Report Summary</h2>
                {error && <p style={{ color: 'red' }}>{error}</p>}

                {token ? (
                    chartData && (
                        <Bar
                            data={chartData}
                            options={{
                                responsive: true,
                                plugins: {
                                    legend: {
                                        display: false,
                                    },
                                    tooltip: {
                                        enabled: true,
                                    },
                                },
                                scales: {
                                    y: {
                                        beginAtZero: true,
                                    },
                                },
                            }}
                        />
                    )
                ) : (
                    <p>Please log in to view the dashboard chart.</p>
                )}

                {/* ✅ REPORTS TABLE */}
                {token && reports.length > 0 && (
                    <div style={{ marginTop: '40px' }}>
                        <h3>All Reports</h3>
                        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                            <thead>
                                <tr>
                                    <th style={thStyle}>Category</th>
                                    <th style={thStyle}>Location</th>
                                    <th style={thStyle}>Status</th>
                                    <th style={thStyle}>Description</th>
                                    <th style={thStyle}>Reported On</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reports.map((report) => (
                                    <tr key={report._id}>
                                        <td style={tdStyle}>{report.category.name}</td>
                                        <td style={tdStyle}>{report.location.name}</td>
                                        <td style={tdStyle}>{report.status.name}</td>
                                        <td style={tdStyle}>{report.description}</td>
                                        <td style={tdStyle}>{new Date(report.reported).toLocaleDateString()}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>
        </>
    );
};

export default DashboardPage;
