import { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend,
} from 'chart.js';
import { jwtDecode } from 'jwt-decode';

ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardPage = () => {
    const [chartData, setChartData] = useState(null);
    const [reports, setReports] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('All');
    const [searchTerm, setSearchTerm] = useState('');
    const [error, setError] = useState('');
    const [userRole, setUserRole] = useState('citizen');
    const token = localStorage.getItem('token');

    const capitalize = (text) =>
        text ? text.charAt(0).toUpperCase() + text.slice(1).toLowerCase() : 'N/A';

    useEffect(() => {
        if (token) {
            try {
                const decoded = jwtDecode(token);
                setUserRole(decoded?.role || 'citizen');
            } catch (err) {
                console.error('Failed to decode token:', err);
            }
        }
    }, [token]);

    const updateChartData = (reportList) => {
        const statusCounts = {};
        reportList.forEach((report) => {
            const status = report?.status?.name || 'Unknown';
            statusCounts[status] = (statusCounts[status] || 0) + 1;
        });

        const statusLabels = Object.keys(statusCounts);
        const statusValues = Object.values(statusCounts);

        setChartData({
            labels: statusLabels,
            datasets: [
                {
                    label: 'Reports by Status',
                    data: statusValues,
                    backgroundColor: [
                        '#82ca9d',
                        '#f39c12',
                        '#3498db',
                        '#e74c3c',
                        '#9b59b6',
                        '#2ecc71',
                    ],
                    borderColor: '#fff',
                    borderWidth: 1,
                },
            ],
        });
    };

    useEffect(() => {
        if (!token) return;

        const fetchReports = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/reports', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const allCategories = new Set();
                res.data.forEach((report) => {
                    const category = report?.category?.name || 'Unknown';
                    allCategories.add(category);
                });

                setReports(res.data);
                setCategories(['All', ...Array.from(allCategories)]);
                updateChartData(res.data);
            } catch (err) {
                console.error(err);
                setError('Failed to load reports');
            }
        };

        fetchReports();
    }, [token]);

    const handleDelete = async (id) => {
        const confirm = window.confirm('Are you sure you want to delete this report?');
        if (!confirm) return;

        try {
            await axios.delete(`http://localhost:5000/api/reports/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });

            const updatedReports = reports.filter((r) => r._id !== id);
            setReports(updatedReports);
            updateChartData(updatedReports);
        } catch (err) {
            console.error('Delete failed:', err.response?.data || err.message);
            alert('Failed to delete report.');
        }
    };

    const handleEdit = (id) => {
        window.location.href = `/edit-report/${id}`;
    };

    const filteredReports = reports.filter((r) => {
        const matchesCategory =
            selectedCategory === 'All' ||
            r?.category?.name?.toLowerCase() === selectedCategory.toLowerCase();

        const matchesSearch = r?.description
            ?.toLowerCase()
            .includes(searchTerm.toLowerCase());

        return matchesCategory && matchesSearch;
    });

    const thStyle = {
        borderBottom: '1px solid #ccc',
        textAlign: 'left',
        padding: '8px',
        backgroundColor: '#f4f4f4',
    };

    const tdStyle = {
        borderBottom: '1px solid #eee',
        padding: '8px',
    };

    return (
        <>
            <nav
                style={{
                    backgroundColor: '#2c3e50',
                    color: 'white',
                    padding: '15px 30px',
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                }}
            >
                <h1 style={{ margin: 0 }}>SafeCity</h1>
                <div>
                    {token ? (
                        <button
                            style={{
                                backgroundColor: '#e74c3c',
                                color: 'white',
                                border: 'none',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'pointer',
                            }}
                            onClick={() => {
                                localStorage.removeItem('token');
                                window.location.reload();
                            }}
                        >
                            Logout
                        </button>
                    ) : (
                        <button
                            style={{
                                backgroundColor: '#3498db',
                                color: 'white',
                                border: 'none',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'pointer',
                            }}
                            onClick={() => (window.location.href = '/login')}
                        >
                            Login
                        </button>
                    )}
                </div>
            </nav>

            <div style={{ display: 'flex', justifyContent: 'center' }}>
                <div style={{ width: '90%', maxWidth: '1000px', padding: '20px' }}>
                    <h2 style={{ textAlign: 'center' }}>Report Summary</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>}

                    {token ? (
                        chartData && (
                            <div
                                style={{
                                    width: '100%',
                                    maxWidth: '600px',
                                    margin: '0 auto',
                                    height: '350px',
                                }}
                            >
                                <Pie
                                    data={chartData}
                                    options={{
                                        maintainAspectRatio: false,
                                        responsive: true,
                                        plugins: {
                                            legend: {
                                                position: 'bottom',
                                                align: 'center',
                                                labels: {
                                                    usePointStyle: true,
                                                    boxWidth: 12,
                                                    padding: 15,
                                                },
                                            },
                                            tooltip: { enabled: true },
                                        },
                                    }}
                                />
                            </div>
                        )
                    ) : (
                        <p style={{ textAlign: 'center' }}>
                            Please log in to view the dashboard.
                        </p>
                    )}

                    {token && (
                        <div style={{ marginTop: '40px' }}>
                            <h3 style={{ textAlign: 'center' }}>All Reports</h3>

                            {/* 🔗 Create New Link */}
                            <div style={{ margin: '10px 0', textAlign: 'left' }}>
                                <a
                                    href="/create-report"
                                    style={{
                                        color: '#3498db',
                                        fontSize: '16px',
                                        textDecoration: 'none',
                                    }}
                                >
                                    Create New
                                </a>
                            </div>

                            {/* 🔽 Dropdown Filter */}
                            <div style={{ marginBottom: '15px', textAlign: 'center' }}>
                                <select
                                    value={selectedCategory}
                                    onChange={(e) => setSelectedCategory(e.target.value)}
                                    style={{
                                        padding: '8px',
                                        borderRadius: '4px',
                                        border: '1px solid #ccc',
                                        minWidth: '200px',
                                    }}
                                >
                                    {categories.map((cat) => (
                                        <option key={cat} value={cat}>
                                            {capitalize(cat)}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            {/* 🔍 Search Filter */}
                            <div style={{ marginBottom: '15px', textAlign: 'center' }}>
                                <input
                                    type="text"
                                    placeholder="Search description..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                    style={{
                                        padding: '8px',
                                        borderRadius: '4px',
                                        border: '1px solid #ccc',
                                        width: '60%',
                                        maxWidth: '400px',
                                    }}
                                />
                            </div>

                            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                                <thead>
                                    <tr>
                                        <th style={thStyle}>Category</th>
                                        <th style={thStyle}>Location</th>
                                        <th style={thStyle}>Status</th>
                                        <th style={thStyle}>Description</th>
                                        <th style={thStyle}>Reported On</th>
                                        {(userRole === 'admin' || userRole === 'officer') && (
                                            <th style={thStyle}></th>
                                        )}
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredReports.length > 0 ? (
                                        filteredReports.map((report) => (
                                            <tr key={report._id}>
                                                <td style={tdStyle}>{capitalize(report?.category?.name)}</td>
                                                <td style={tdStyle}>{capitalize(report?.location?.name)}</td>
                                                <td style={tdStyle}>{capitalize(report?.status?.name)}</td>
                                                <td style={tdStyle}>{report?.description || 'N/A'}</td>
                                                <td style={tdStyle}>
                                                    {report?.reported
                                                        ? new Date(report.reported).toLocaleDateString()
                                                        : 'N/A'}
                                                </td>
                                                {(userRole === 'admin' || userRole === 'officer') && (
                                                    <td style={tdStyle}>
                                                        <button
                                                            onClick={() => handleEdit(report._id)}
                                                            style={{
                                                                backgroundColor: '#f39c12',
                                                                color: 'white',
                                                                border: 'none',
                                                                padding: '6px 10px',
                                                                borderRadius: '4px',
                                                                cursor: 'pointer',
                                                                marginRight: '6px',
                                                            }}
                                                        >
                                                            Edit
                                                        </button>
                                                        {userRole === 'admin' && (
                                                            <button
                                                                onClick={() => handleDelete(report._id)}
                                                                style={{
                                                                    backgroundColor: '#e74c3c',
                                                                    color: 'white',
                                                                    border: 'none',
                                                                    padding: '6px 10px',
                                                                    borderRadius: '4px',
                                                                    cursor: 'pointer',
                                                                }}
                                                            >
                                                                Delete
                                                            </button>
                                                        )}
                                                    </td>
                                                )}
                                            </tr>
                                        ))
                                    ) : (
                                        <tr>
                                            <td
                                                colSpan={userRole === 'admin' || userRole === 'officer' ? 6 : 5}
                                                style={{
                                                    textAlign: 'center',
                                                    padding: '20px',
                                                    color: '#999',
                                                }}
                                            >
                                                No reports in this category.
                                            </td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>
        </>
    );
};

export default DashboardPage;
