import { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardPage = () => {
    const [chartData, setChartData] = useState(null);
    const [reports, setReports] = useState([]);
    const [filteredReports, setFilteredReports] = useState([]);
    const [search, setSearch] = useState('');
    const [error, setError] = useState('');
    const token = localStorage.getItem('token');

    useEffect(() => {
        if (!token) return;

        const fetchReports = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/reports', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const counts = {};
                res.data.forEach((report) => {
                    const categoryName = report.category.name;
                    counts[categoryName] = (counts[categoryName] || 0) + 1;
                });

                const labels = Object.keys(counts).map(label =>
                    label.charAt(0).toUpperCase() + label.slice(1).toLowerCase()
                );
                const values = Object.values(counts);

                setChartData({
                    labels: labels,
                    datasets: [
                        {
                            label: 'Report Count by Category',
                            data: values,
                            backgroundColor: [
                                '#82ca9d',
                                '#f39c12',
                                '#3498db',
                                '#e74c3c',
                                '#9b59b6',
                                '#2ecc71'
                            ],
                            borderColor: '#ffffff',
                            borderWidth: 1,
                        },
                    ],
                });

                setReports(res.data);
                setFilteredReports(res.data);
            } catch (err) {
                setError('Failed to load reports');
                console.error(err);
            }
        };

        fetchReports();
    }, [token]);

    useEffect(() => {
        const keyword = search.toLowerCase();
        const filtered = reports.filter((report) =>
            report.category.name.toLowerCase().includes(keyword) ||
            report.location.name.toLowerCase().includes(keyword) ||
            report.status.name.toLowerCase().includes(keyword) ||
            report.description.toLowerCase().includes(keyword)
        );
        setFilteredReports(filtered);
    }, [search, reports]);

    const thStyle = {
        borderBottom: '1px solid #ccc',
        textAlign: 'left',
        padding: '8px',
        backgroundColor: '#f4f4f4',
    };

    const tdStyle = {
        borderBottom: '1px solid #eee',
        padding: '8px',
    };

    return (
        <>
            {/* ✅ NAVBAR */}
            <nav style={{
                backgroundColor: '#2c3e50',
                color: 'white',
                padding: '15px 30px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
            }}>
                <h1 style={{ margin: 0 }}>SafeCity</h1>
                <div>
                    {token ? (
                        <button
                            style={{
                                backgroundColor: '#e74c3c',
                                color: 'white',
                                border: 'none',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                            onClick={() => {
                                localStorage.removeItem('token');
                                window.location.reload();
                            }}
                        >
                            Logout
                        </button>
                    ) : (
                        <button
                            style={{
                                backgroundColor: '#3498db',
                                color: 'white',
                                border: 'none',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                            onClick={() => window.location.href = '/login'}
                        >
                            Login
                        </button>
                    )}
                </div>
            </nav>

            {/* ✅ CONTENT */}
            <div style={{ display: 'flex', justifyContent: 'center' }}>
                <div style={{ width: '90%', maxWidth: '1000px', padding: '20px' }}>
                    <h2 style={{ textAlign: 'center' }}>Report Summary</h2>
                    {error && <p style={{ color: 'red' }}>{error}</p>}

                    {token ? (
                        chartData && (
                            <div style={{ maxWidth: '300px', margin: '0 auto' }}>
                                <Pie
                                    data={chartData}
                                    options={{
                                        maintainAspectRatio: false,
                                        plugins: {
                                            legend: {
                                                position: 'bottom',
                                                labels: {
                                                    usePointStyle: true,
                                                    boxWidth: 10,
                                                    padding: 20,
                                                }
                                            },
                                            tooltip: {
                                                enabled: true,
                                            },
                                        },
                                    }}
                                    width={300}
                                    height={300}
                                />
                            </div>
                        )
                    ) : (
                        <p style={{ textAlign: 'center' }}>Please log in to view the dashboard chart.</p>
                    )}

                    {/* ✅ REPORTS TABLE */}
                    {token && filteredReports.length > 0 && (
                        <div style={{ marginTop: '40px' }}>
                            <h3 style={{ textAlign: 'center' }}>All Reports</h3>

                            {/* 🔍 Search Filter */}
                            <input
                                type="text"
                                placeholder="Search reports..."
                                value={search}
                                onChange={(e) => setSearch(e.target.value)}
                                style={{
                                    marginBottom: '15px',
                                    padding: '8px',
                                    width: '100%',
                                    maxWidth: '300px',
                                    borderRadius: '4px',
                                    border: '1px solid #ccc'
                                }}
                            />

                            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                                <thead>
                                    <tr>
                                        <th style={thStyle}>Category</th>
                                        <th style={thStyle}>Location</th>
                                        <th style={thStyle}>Status</th>
                                        <th style={thStyle}>Description</th>
                                        <th style={thStyle}>Reported On</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {filteredReports.map((report) => (
                                        <tr key={report._id}>
                                            <td style={tdStyle}>{report.category.name}</td>
                                            <td style={tdStyle}>{report.location.name}</td>
                                            <td style={tdStyle}>{report.status.name}</td>
                                            <td style={tdStyle}>{report.description}</td>
                                            <td style={tdStyle}>{new Date(report.reported).toLocaleDateString()}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>
        </>
    );
};

export default DashboardPage;
