import { useEffect, useState } from 'react';
import axios from 'axios';

const CreateReportPage = () => {
    const [categoryOptions, setCategoryOptions] = useState([]);
    const [statusOptions, setStatusOptions] = useState([]);
    const [locationOptions, setLocationOptions] = useState([]);
    const [form, setForm] = useState({
        category: '',
        status: '',
        location: '',
        description: ''
    });
    const [message, setMessage] = useState('');
    const token = localStorage.getItem('token');

    // Fetch dropdown options
    useEffect(() => {
        const fetchOptions = async () => {
            try {
                const headers = { Authorization: `Bearer ${token}` };

                const [categoriesRes, statusesRes, locationsRes] = await Promise.all([
                    axios.get('http://localhost:5000/api/categories', { headers }),
                    axios.get('http://localhost:5000/api/statuses', { headers }),
                    axios.get('http://localhost:5000/api/locations', { headers }),
                ]);

                setCategoryOptions(categoriesRes.data);
                setStatusOptions(statusesRes.data);
                setLocationOptions(locationsRes.data);
            } catch (err) {
                console.error('Failed to fetch dropdowns:', err);
            }
        };

        fetchOptions();
    }, [token]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const user = JSON.parse(atob(token.split('.')[1]))?.id; // Get user ID from token
            const res = await axios.post(
                'http://localhost:5000/api/reports',
                { ...form, user },
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );

            setMessage('✅ Report submitted successfully.');
            setForm({ category: '', status: '', location: '', description: '' });
        } catch (err) {
            console.error(err);
            setMessage('❌ Failed to submit report.');
        }
    };

    return (
        <div style={{ maxWidth: '600px', margin: '40px auto', padding: '20px' }}>
            <h2>Create Report</h2>
            {message && <p>{message}</p>}

            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '15px' }}>
                    <label>Category:</label>
                    <select
                        name="category"
                        value={form.category}
                        onChange={handleChange}
                        required
                        style={{ width: '100%', padding: '8px' }}
                    >
                        <option value="">-- Select Category --</option>
                        {categoryOptions.map((c) => (
                            <option key={c._id} value={c._id}>
                                {c.name}
                            </option>
                        ))}
                    </select>
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Status:</label>
                    <select
                        name="status"
                        value={form.status}
                        onChange={handleChange}
                        required
                        style={{ width: '100%', padding: '8px' }}
                    >
                        <option value="">-- Select Status --</option>
                        {statusOptions.map((s) => (
                            <option key={s._id} value={s._id}>
                                {s.name}
                            </option>
                        ))}
                    </select>
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Location:</label>
                    <select
                        name="location"
                        value={form.location}
                        onChange={handleChange}
                        required
                        style={{ width: '100%', padding: '8px' }}
                    >
                        <option value="">-- Select Location --</option>
                        {locationOptions.map((l) => (
                            <option key={l._id} value={l._id}>
                                {l.name}
                            </option>
                        ))}
                    </select>
                </div>

                <div style={{ marginBottom: '15px' }}>
                    <label>Description:</label>
                    <textarea
                        name="description"
                        value={form.description}
                        onChange={handleChange}
                        required
                        style={{ width: '100%', padding: '8px' }}
                        rows={4}
                    />
                </div>

                <button
                    type="submit"
                    style={{
                        backgroundColor: '#2ecc71',
                        color: 'white',
                        border: 'none',
                        padding: '10px 20px',
                        borderRadius: '4px',
                        cursor: 'pointer',
                    }}
                >
                    Submit Report
                </button>
            </form>
        </div>
    );
};

export default CreateReportPage;
